import "date"
import "math"
import "timezone"

option location = timezone.location(name: "Europe/Brussels")

// --- 1. PR√âPARATION DES DONN√âES √âNERG√âTIQUES ---
// On convertit en √©nergie UNE SEULE FOIS au d√©but
base_energy = from(bucket: "default")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "dt8852" and r._field == "spl" and r.frequency_weighting == "dB(A)" and exists r.time_weighting)
  |> map(fn: (r) => ({ r with _value: math.pow(x: 10.0, y: r._value / 10.0) }))
  |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)

// --- 2. CALCULS DES INDICATEURS PAR JOUR ---
// Fonction helper pour convertir l √©nergie en dB √† la fin
toDB = (val) => if exists val and val > 0.0 then 10.0 * math.log10(x: val) else 0.0

// Branche commune group√©e par jour
daily_base = base_energy |> group(columns: ["frequency_weighting", "time_weighting"])

// Leq (24h)
leq = daily_base 
  |> aggregateWindow(every: 1d, fn: mean, timeSrc: "_start")
  |> map(fn: (r) => ({ r with _field: "Leq", _value: toDB(val: r._value) }))

// Lday (07h - 22h)
lday = daily_base
  |> filter(fn: (r) => {
      h = date.hour(t: r._time)
      return h >= 7 and h < 22
  })
  |> aggregateWindow(every: 1d, fn: mean, timeSrc: "_start")
  |> map(fn: (r) => ({ r with _field: "Lday", _value: toDB(val: r._value) }))

// Lnight (22h - 07h)
lnight = daily_base
  |> filter(fn: (r) => {
      h = date.hour(t: r._time)
      return h >= 22 or h < 7
  })
  |> aggregateWindow(every: 1d, fn: mean, timeSrc: "_start")
  |> map(fn: (r) => ({ r with _field: "Lnight", _value: toDB(val: r._value) }))

// Lden (avec p√©nalit√©s √©nerg√©tiques)
lden = daily_base
  |> map(fn: (r) => {
      h = date.hour(t: r._time)
      penalty_db = if h >= 19 and h < 23 then 5.0 
                   else if h >= 23 or h < 7 then 10.0 
                   else 0.0
      return { r with _value: r._value * math.pow(x: 10.0, y: penalty_db / 10.0) }
  })
  |> aggregateWindow(every: 1d, fn: mean, timeSrc: "_start")
  |> map(fn: (r) => ({ r with _field: "Lden", _value: toDB(val: r._value) }))

// --- 3. STATISTIQUES (Min, Max, Quantiles) ---
// Note : on les calcule sur l √©nergie car c est plus rapide (le r√©sultat en dB sera identique)
stats = daily_base |> aggregateWindow(every: 1d, fn: mean, timeSrc: "_start") // juste pour avoir la structure

lmax = daily_base |> aggregateWindow(every: 1d, fn: max, timeSrc: "_start") |> map(fn: (r) => ({ r with _field: "Lmax", _value: toDB(val: r._value) }))
lmin = daily_base |> aggregateWindow(every: 1d, fn: min, timeSrc: "_start") |> map(fn: (r) => ({ r with _field: "Lmin", _value: toDB(val: r._value) }))
l10  = daily_base |> aggregateWindow(every: 1d, fn: (column, tables=<-) => tables |> quantile(q: 0.90), timeSrc: "_start") |> map(fn: (r) => ({ r with _field: "L10", _value: toDB(val: r._value) }))
l50  = daily_base |> aggregateWindow(every: 1d, fn: (column, tables=<-) => tables |> quantile(q: 0.50), timeSrc: "_start") |> map(fn: (r) => ({ r with _field: "L50", _value: toDB(val: r._value) }))
l90  = daily_base |> aggregateWindow(every: 1d, fn: (column, tables=<-) => tables |> quantile(q: 0.10), timeSrc: "_start") |> map(fn: (r) => ({ r with _field: "L90", _value: toDB(val: r._value) }))

// --- 4. ASSEMBLAGE FINAL ---
union(tables: [l10, l50, l90, lmin, lmax, lden, lday, lnight, leq])
  |> pivot(rowKey: ["_time", "frequency_weighting", "time_weighting"], columnKey: ["_field"], valueColumn: "_value")

import "date"
import "timezone"
import "math"

option location = timezone.location(name: "Europe/Brussels")

// 1. Calcul de la dur√©e s√©lectionn√©e par lutilisateur
rangeDuration = uint(v: v.timeRangeStop) - uint(v: v.timeRangeStart)
oneDay = uint(v: 24h)

// 2. D√©finition du point de d√©part dynamique
// Si la s√©lection est < 24h, on force le d√©but √† minuit aujourdhui
actualStart = if rangeDuration < oneDay then date.truncate(t: now(), unit: 1d) else v.timeRangeStart

// 3. Calcul de la fen√™tre dagr√©gation dynamique pour √©viter le "Too many datapoints"
// On calcule la dur√©e r√©elle de donn√©es quon va charger
actualDuration = uint(v: v.timeRangeStop) - uint(v: actualStart)
// On divise par 1000 pour viser environ 1000 points sur le graphique
divisor = uint(v: 1000)
// On prend le plus grand entre (dur√©e totale / 1000) et la fen√™tre automatique de Grafana
windowInNs = if actualDuration / divisor > uint(v: v.windowPeriod) then actualDuration / divisor else uint(v: v.windowPeriod)
dynamicWindow = duration(v: windowInNs)

from(bucket: "default")
  |> range(start: actualStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "dt8852" and r._field == "spl" and r.frequency_weighting == "dB(A)")
  |> aggregateWindow(every: dynamicWindow, fn: median)
  |> filter(fn: (r) => exists r._value)
  |> keep(columns: ["_time", "_value", "frequency_weighting", "time_weighting"])
    |> map(fn: (r) => {
      h = date.hour(t: r._time)
      m = date.minute(t: r._time)
      s = date.second(t: r._time)

      // Formule pour arrondir √† 0.5 pr√®s
      roundedValue = math.round(x: r._value * 4.0) / 4.0

      // Seuils PAC Wallonie
      limit = if h >= 7 and h < 19 then 50.0 
              else if (h >= 6 and h < 7) or (h >= 19 and h < 22) then 45.0 
              else 40.0

      // On utilise la valeur arrondie pour calculer la s√©v√©rit√© si on veut de la coh√©rence
      diff = if roundedValue > limit then roundedValue - limit else 0.0

      return {
          _time: r._time,
          decibels: roundedValue,
          heure: float(v: h) + (float(v: m) / 60.0) + (float(v: s) / 3600.0),
          limit: limit,
          severite: diff + 1.0
      }
  })
  |> keep(columns: ["_time", "decibels", "heure", "severite", "limit"])
  |> group()

import "math"
import "timezone"

// Configuration de la zone horaire
option location = timezone.location(name: "Europe/Brussels")

base = from(bucket: "default")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "dt8852" and r._field == "spl" and r.frequency_weighting == "dB(A)" and exists r._value and exists r.time_weighting)
  |> keep(columns: ["_time", "_value", "frequency_weighting", "time_weighting"])

// 2. Calcul du L10 (Pics sonores) 
l10 = base
  |> aggregateWindow(
        every: v.windowPeriod,
        fn: (column, tables=<-) => tables |> quantile(q: 0.90),
        createEmpty: true
     )
  |> aggregateWindow(
        every: ${interval},
        fn: mean,
        createEmpty: true
     )
  |> map(fn: (r) => ({ r with _field: "l10" }))

// 3. Calcul du L90 (Bruit de fond)
l90 = base
  |> aggregateWindow(
        every: v.windowPeriod,
        fn: (column, tables=<-) => tables |> quantile(q: 0.10),
        createEmpty: true
     )
  |> aggregateWindow(
        every: ${interval},
        fn: mean,
        createEmpty: true
     )
  |> map(fn: (r) => ({ r with _field: "l90" }))

// 4. Fusion et Pivotage s√©curis√©
union(tables: [l10, l90])
  |> pivot(rowKey:["_time", "frequency_weighting", "time_weighting"], columnKey: ["_field"], valueColumn: "_value")
  |> filter(fn: (r) => exists r.l10 and exists r.l90)
  |> map(fn: (r) => {
      val_l10 = float(v: r.l10)
      val_l90 = float(v: r.l90)
      diff = val_l10 - val_l90

      return {
          _time: r._time,
          frequency_weighting: r.frequency_weighting,
          time_weighting: r.time_weighting,
          _field: "Climat Sonore (L10-L90)",
          _value: if diff < 0.0 then 0.0 else diff
      }
  })
  |> yield(name: "climat_sonore")
