targets.query:
import "math"
import "timezone"

option location = timezone.location(name: "Europe/Brussels")

from(bucket: "default")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "dt8852")
  |> filter(fn: (r) => r["_field"] == "spl")
  // On garde la colonne frequency_weighting
  |> keep(columns: ["_time", "_value", "_field", "frequency_weighting"])

  // Passage en échelle linéaire (énergie)
  |> map(fn: (r) => ({
      r with 
      _value: math.pow(x: 10.0, y: r._value / 10.0)
  }))

  // Calcul de la MOYENNE énergétique (Leq) par minute
  // Note: mean est indispensable ici pour le calcul logarithmique
  |> aggregateWindow(every: ${average_window}, fn: median, createEmpty: false)

  // Retour en décibels ET renommage du flux
  |> map(fn: (r) => ({
      r with 
      _value: 10.0 * math.log10(x: r._value),
      _field: "Sound Pressure Level"
  }))

  // Lissage final (optionnel selon vos besoins visuels)
  |> exponentialMovingAverage(n: int(v: ${exponential_n})) 
  |> yield(name: "spl_result")

import "date"
import "timezone"

option location = timezone.location(name: "Europe/Brussels")

from(bucket: "default")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "dt8852" and r._field == "spl")
  |> aggregateWindow(every: v.windowPeriod, fn: first)
  |> map(fn: (r) => {
      h = date.hour(t: r._time)
      return {
          _time: r._time,
          _limit: if h >= 7 and h < 19 then 50.0 
                  else if h == 6 or (h >= 19 and h < 22) then 45.0 
                  else 40.0
      }
  })

import "math"
import "timezone"
import "date"

option location = timezone.location(name: "Europe/Brussels")

from(bucket: "default")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "dt8852" and r["_field"] == "spl")
  // 1. Keep data volume safe (10s resolution)
  |> aggregateWindow(every: 10s, fn: last, createEmpty: false)
  |> map(fn: (r) => {
      h = date.hour(t: r._time)

      // 2. Define the four distinct periods
      period = if h >= 7 and h < 19 then "Day (Limit 50dB)"
               else if h >= 19 and h < 22 then "Dusk / Evening (Limit 45dB)"
               else if h >= 6 and h < 7 then "Dawn / Morning (Limit 45dB)"
               else "Night (Limit 40dB)"

      return { r with 
          // 3. Bucket by 3dB for acoustic significance
          _value: math.floor(x: r._value / 3.0) * 3.0,
          // 4. Use the period name as the field for the histogram series
          _field: period
      }
  })
  |> keep(columns: ["_time", "_value", "_field"])
