import "math"
import "timezone"
import "date"

option location = timezone.location(name: "Europe/Brussels")

from(bucket: "default")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "dt8852" and r["_field"] == "spl")
  |> filter(fn: (r) => exists r._value)

  //|> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)
  |> aggregateWindow(every: v.windowPeriod, fn: (column, tables=<-) => tables |> quantile(q: 0.50), createEmpty: false)
  // 3. Calcul des limites horaires et du dépassement
  |> map(fn: (r) => {
      h = date.hour(t: r._time)

      // Seuils réglementaires (50 / 45 / 40 dB)
      limite = if h >= 7 and h < 19 then 50.0 
               else if (h >= 6 and h < 7) or (h >= 19 and h < 22) then 45.0 
               else 40.0

      // Calcul du dépassement
      diff = if r._value > limite then r._value - limite else 0.0

      return {
          _time: r._time,
          _value: diff,
          _field: "Dépassement",
          niveau_reel: r._value,
          seuil_legal: limite
      }
  })

  // 4. Envoi des données vers Grafana
  |> yield(name: "depassement_acoustique")

import "math"
import "timezone"
import "date"

option location = timezone.location(name: "Europe/Brussels")

from(bucket: "default")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "dt8852" and r["_field"] == "spl")
  // 1. Keep data volume safe (10s resolution)
  |> aggregateWindow(every: 10s, fn: last, createEmpty: false)
  |> map(fn: (r) => {
      h = date.hour(t: r._time)

      // 2. Define the four distinct periods
      period = if h >= 7 and h < 19 then "Jour (Limite 50dB)"
               else if h >= 19 and h < 22 then "Soirée (Limite 45dB)"
               else if h >= 6 and h < 7 then "Aube / Matinée (Limite 45dB)"
               else "Nuit (Limite 40dB)"

      return { r with 
          // 3. Bucket by 3dB for acoustic significance
          _value: math.floor(x: r._value / 3.0) * 3.0,
          // 4. Use the period name as the field for the histogram series
          _field: period
      }
  })
  |> keep(columns: ["_time", "_value", "_field"])

import "math"
import "timezone"

// Configuration de la zone horaire
option location = timezone.location(name: "Europe/Brussels")

// 1. Récupération des données de base
base = from(bucket: "default")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "dt8852" and r["_field"] == "spl")
  |> filter(fn: (r) => exists r._value)

// 2. Calcul du L10 (Pics sonores) avec renommage du champ
l10 = base
  |> aggregateWindow(every: 30m, fn: (column, tables=<-) => tables |> quantile(q: 0.90), createEmpty: false)
  |> map(fn: (r) => ({ r with _field: "l10" }))

// 3. Calcul du L90 (Bruit de fond) avec renommage du champ
l90 = base
  |> aggregateWindow(every: 30m, fn: (column, tables=<-) => tables |> quantile(q: 0.10), createEmpty: false)
  |> map(fn: (r) => ({ r with _field: "l90" }))

// 4. Fusion des tables et Pivotage pour mettre L10 et L90 sur la même ligne
union(tables: [l10, l90])
  |> pivot(rowKey:["_time", "frequency_weighting"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => {
      // Calcul de la différence entre pics et bruit de fond
      raw_diff = r.l10 - r.l90

      return {
          _time: r._time,
          frequency_weighting: r.frequency_weighting,
          _field: "Climat Sonore (L10-L90)",
          _value: if raw_diff < 0.0 then 0.0 else raw_diff
      }
  })
