import "timezone"
import "date"

option location = timezone.location(name: "Europe/Brussels")

from(bucket: "default")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "dt8852" and r._field == "spl" and r.frequency_weighting == "dB(A)")
  |> filter(fn: (r) => exists r._value)
  |> filter(fn: (r) => exists r.time_weighting)  
  |> aggregateWindow(
      every: 60s,
      fn: (column, tables=<-) => tables |> quantile(q: 0.90, column: column),
      createEmpty: true
  )
  |> map(fn: (r) => {
      h = date.hour(t: r._time)

      limit =
        if h >= 7 and h < 19 then 50.0
        else if (h >= 6 and h < 7) or (h >= 19 and h < 22) then 45.0
        else 40.0

      return {
        _time: r._time,
        _value: if r._value > limit then 1.0 else 0.0,
        is_violation: if r._value > limit then 1 else 0
      }
  })
  |> stateDuration(fn: (r) => r.is_violation == 1, column: "duration", unit: 1s)
  |> map(fn: (r) => ({
      _time: r._time,
      _value: if r.is_violation == 1 and r.duration >= 120 then 1.0 else 0.0
  }))

