  |> aggregateWindow(every: v.windowPeriod, fn: (column, tables=<-) => tables |> quantile(q: 0.50), createEmpty: true)
  // 3. Calcul des limites horaires et du d√©passement
  |> map(fn: (r) => {
      h = date.hour(t: r._time)

      // Seuils r√©glementaires (50 / 45 / 40 dB)
      limite = if h >= 7 and h < 19 then 50.0 
               else if (h >= 6 and h < 7) or (h >= 19 and h < 22) then 45.0 
               else 40.0

      // Calcul du d√©passement
      diff = if r._value > limite then r._value - limite else 0.0

      return {
