import "timezone"
import "date"

option location = timezone.location(name: "Europe/Brussels")

from(bucket: "default")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "dt8852" and r._field == "spl" and r.frequency_weighting == "dB(A)")
  |> filter(fn: (r) => exists r._value)
  |> filter(fn: (r) => exists r.time_weighting)  
  |> aggregateWindow(
      every: 60s,
      fn: (column, tables=<-) => tables |> quantile(q: 0.90, column: column),
      createEmpty: true
  )
  |> map(fn: (r) => {
      h = date.hour(t: r._time)

      limit =
        if h >= 7 and h < 19 then 50.0
        else if (h >= 6 and h < 7) or (h >= 19 and h < 22) then 45.0
        else 40.0

      return {
        _time: r._time,
        _value: if r._value > limit then 1.0 else 0.0,
        is_violation: if r._value > limit then 1 else 0
      }
  })
  |> stateDuration(fn: (r) => r.is_violation == 1, column: "duration", unit: 1s)
  |> map(fn: (r) => ({
      _time: r._time,
      _value: if r.is_violation == 1 and r.duration >= 120 then 1.0 else 0.0
  }))

import "math"
import "timezone"
import "date"

option location = timezone.location(name: "Europe/Brussels")

from(bucket: "default")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "dt8852" and r._field == "spl" and r.frequency_weighting == "dB(A)")
  |> filter(fn: (r) => exists r._value)

  //|> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: true)
  |> aggregateWindow(every: v.windowPeriod, fn: (column, tables=<-) => tables |> quantile(q: 0.50), createEmpty: true)
  // 3. Calcul des limites horaires et du dépassement
  |> map(fn: (r) => {
      h = date.hour(t: r._time)

      // Seuils réglementaires (50 / 45 / 40 dB)
      limite = if h >= 7 and h < 19 then 50.0 
               else if (h >= 6 and h < 7) or (h >= 19 and h < 22) then 45.0 
               else 40.0

      // Calcul du dépassement
      diff = if r._value > limite then r._value - limite else 0.0

      return {
          _time: r._time,
          _value: diff,
          _field: "Dépassement",
          niveau_reel: r._value,
          seuil_legal: limite
      }
  })

  // 4. Envoi des données vers Grafana
  |> yield(name: "depassement_acoustique")

import "date"
import "math"
import "timezone"

option location = timezone.location(name: "Europe/Brussels")

// Utilisation des variables Grafana
startDate = 2026-01-15T07:00:00Z
stopDate = date.truncate(t: now(), unit: 1d)

// 1. PRÉ-RÉDUCTION (L'unique passage sur les données brutes)
base1m = from(bucket: "default")
  |> range(start: startDate, stop: stopDate)
  |> filter(fn: (r) => r._measurement == "dt8852" and r._field == "spl")
  |> filter(fn: (r) => r.frequency_weighting == "dB(A)")
  |> filter(fn: (r) => exists r._value and exists r.time_weighting)
  |> aggregateWindow(every: 1m, fn: mean) 
  // --- CORRECTIF : On élimine les minutes vides générées par aggregateWindow ---
  |> filter(fn: (r) => exists r._value)
  // ---------------------------------------------------------------------------
  |> keep(columns: ["_time", "_value", "frequency_weighting", "time_weighting"])

// 2. CALCULS DES EXTRÊMES
lmax = base1m 
  |> aggregateWindow(every: 1d, fn: max)
  |> map(fn: (r) => ({ r with _field: "Lmax" }))

lmin = base1m 
  |> aggregateWindow(every: 1d, fn: min)
  |> map(fn: (r) => ({ r with _field: "Lmin" }))

// 3. CALCUL LDEN
lden = base1m
  |> map(fn: (r) => {
      h = date.hour(t: r._time)
      // On s'assure que p est bien un float (0.0, 5.0, 10.0)
      p = if h >= 7 and h < 19 then 0.0 else if h >= 19 and h < 22 then 5.0 else 10.0
      return { r with energy: math.pow(x: 10.0, y: (float(v: r._value) + p) / 10.0) }
  })
  |> aggregateWindow(every: 1d, fn: mean, column: "energy")
  |> map(fn: (r) => ({ 
      _time: r._time,
      frequency_weighting: r.frequency_weighting,
      time_weighting: r.time_weighting,
      _field: "Lden", 
      _value: 10.0 * math.log10(x: r.energy) 
  }))

// 4. CALCUL PERCENTILES
l10 = base1m |> aggregateWindow(every: 1d, fn: (column, tables=<-) => tables |> quantile(q: 0.90)) |> map(fn: (r) => ({ r with _field: "L10" }))
l50 = base1m |> aggregateWindow(every: 1d, fn: (column, tables=<-) => tables |> quantile(q: 0.50)) |> map(fn: (r) => ({ r with _field: "L50" }))
l90 = base1m |> aggregateWindow(every: 1d, fn: (column, tables=<-) => tables |> quantile(q: 0.10)) |> map(fn: (r) => ({ r with _field: "L90" }))

// 5. ASSEMBLAGE FINAL
union(tables: [l10, l50, l90, lmin, lmax, lden])
  |> pivot(rowKey: ["_time", "frequency_weighting", "time_weighting"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({ 
      r with 
      _time: date.truncate(t: r._time, unit: 1d)
  }))

import "math"
import "timezone"

// Configuration de la zone horaire
option location = timezone.location(name: "Europe/Brussels")

// 1. Récupération des données avec nettoyage immédiat
// Le "keep" permet de libérer énormément de mémoire vive
base = from(bucket: "default")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "dt8852" and r._field == "spl" and r.frequency_weighting == "dB(A)")
  |> filter(fn: (r) => exists r._value and exists r.time_weighting)
  |> keep(columns: ["_time", "_value", "frequency_weighting", "time_weighting"])

// 2. Calcul du L10 (Pics sonores) 
// On enlève createEmpty: true pour ne pas traiter de lignes vides inutiles
l10 = base
  |> aggregateWindow(every: 10m, fn: (column, tables=<-) => tables |> quantile(q: 0.90))
  |> map(fn: (r) => ({ r with _field: "l10" }))

// 3. Calcul du L90 (Bruit de fond)
l90 = base
  |> aggregateWindow(every: 10m, fn: (column, tables=<-) => tables |> quantile(q: 0.10))
  |> map(fn: (r) => ({ r with _field: "l90" }))

// 4. Fusion et Pivotage sécurisé
union(tables: [l10, l90])
  |> pivot(rowKey:["_time", "frequency_weighting", "time_weighting"], columnKey: ["_field"], valueColumn: "_value")
  // On filtre les lignes où l'un des deux calculs serait manquant
  |> filter(fn: (r) => exists r.l10 and exists r.l90)
  |> map(fn: (r) => {
      // Conversion explicite en float pour éviter l'erreur "invalid"
      val_l10 = float(v: r.l10)
      val_l90 = float(v: r.l90)
      diff = val_l10 - val_l90

      return {
          _time: r._time,
          frequency_weighting: r.frequency_weighting,
          time_weighting: r.time_weighting,
          _field: "Climat Sonore (L10-L90)",
          _value: if diff < 0.0 then 0.0 else diff
      }
  })
  |> yield(name: "climat_sonore")
